import React from "react";
import AliceCarousel from "react-alice-carousel";
import "react-alice-carousel/lib/alice-carousel.css";
import ProCrouselData from "./ProCrouselData";
import "./Home.css";

const responsive = {
  0: { items: 1 },
  700: { items: 2 },
  1024: { items: 4 },
};

const ProCrousel = () => {
  return (
    <>
      <div className="Client_section">
        <div className="container">
          <div className="row">
            <AliceCarousel
              responsive={responsive}
              autoPlay={true}
              infinite={true}
              autoPlayInterval={1500}
              disableButtonsControls={true}
            >
              {ProCrouselData.map((data) => {
                return (
                  <>
                    <div className="item">
                      <div className="OurClient" key={data.id}>
                       <div className="testimonial_img">
                       <img src={data.img} alt="company_logo" className="w-100" />
                       </div>
                       <div className="testimonial_content">
                          <h1>{data.Title}</h1>
                          <p>{data.disc}</p>
                       </div>
                      </div>
                    </div>
                  </>
                );
              })}
            </AliceCarousel>
          </div>
        </div>
      </div>
    </>
  );
};

export default ProCrousel;
