import React from "react";
import AliceCarousel from "react-alice-carousel";
import "react-alice-carousel/lib/alice-carousel.css";
import CardData from "./ServicesData";
import "./Home.css";

const responsive = {
  0: { items: 1 },
  700: { items: 2 },
  1024: { items: 5 },
};

const ProCrousel = () => {
 const  clientsection = [
    {
      id:1,
      img:"http://airmaker.in/images/client/trident.jpg",
    },
    {
      id:2,
      img:"http://airmaker.in/images/client/ncc.jpg",
    },
    {
      id:3,
      img:"http://airmaker.in/images/client/ginni.jpg",
    },
    {
      id:4,
      img:"http://airmaker.in/images/client/lt.png",
    },
    {
      id:5,
      img:"http://airmaker.in/images/client/tt.jpg",
    },
     {
      id:6,
      img:"http://airmaker.in/images/client/sathia.png",
    },
  ]
  return (
    <>
      <div className="Client_section">
        <div className="container">
          <div className="row">
            <AliceCarousel
              mouseTracking
              responsive={responsive}
              autoPlay={true}
              infinite={true}
              autoPlayInterval={1500}
              disableButtonsControls={true}
            >
              {clientsection.map((data) => {
                return (
                  <>
                    {/* <div className='col-md-6 col-lg-6 gap-2 bg-danger w-100'> */}
                    <div className="item">
                      <div className="OurClient" key={id}>
                        <img src={data.img} alt="company_logo" className="w-100" />
                      </div>
                    </div>
                    {/* </div> */}
                  </>
                );
              })}
            </AliceCarousel>
          </div>
        </div>
      </div>
    </>
  );
};

export default ProCrousel;
